import React, {useRef, useEffect} from 'react'
import * as THREE from "three";

const Grid = (props) => {

const grids = [];
const face = [1,0,0];

const Square = (props) => {
 
const ref = useRef();
useEffect(() => void ref.current.lookAt(new THREE.Vector3(0,1,0)), [ref.current])


    return(<mesh rotation={[3, 0, 0]} position={props.position} ref={ref}>
        <planeGeometry attach="geometry" args={[1,1]} />
        <meshBasicMaterial color="grey" attach="material" />
    </mesh>)
}


for(var i = 0; i<props.width; i++) {
    for (var j = 0; j<props.length; j++) {
        grids.push(<Square position={[i,0,j]}/>)
    }
}

    return (
        <>
          {grids}  
        </>
    )
}

export default Grid
