import React, {useRef, useEffect} from 'react'

const Grid = (props) => {

const grids = [];
const face = [0,1,0];

const Square = (props) => {
const vertices = [props.position.x + 1, 0, props.position.z + 1];
const ref = useRef();
useEffect(() => {ref.current.lookAt(0,1,0)}, [ref.current])


    return(<mesh position={props.position} ref={ref}>
        <geometry attach="geometry" vertices={vertices} faces={face} />
        <meshBasicMaterial color="grey" attach="material" />
    </mesh>)
}


for(var i = 0; i<props.width; i++) {
    for (var j = 0; j<props.length; j++) {
        grids.push(<Square position={[i,0,j]}/>)
    }
}

    return (
        <>
          {grids}  
        </>
    )
}

export default Grid
