import React, { Fragment } from "react";
 
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Translations from '../resources/Translations.js';
import './page.css';

function loadImage(loader) {
    var preview = document.getElementById('preview');
   // var loader = document.getElementById('image-loader');
    var image;
    if (loader.files.length > 0) {
        image = loader.files[0];
    }
    
    var reader = new FileReader();
    
    reader.onloadend = () => {
        preview.src = reader.result;
    }

    if (image) {
        reader.readAsDataURL(image);
    } else {
        preview.src = "";
    }
}

const CreatePoll = () => {

    const style = {
        marginTop: '40px',
 
    }

    const textStyle = {
       // width: '700px'
    }



    return(
        <Container id='cont-area'>
        <Row>
            <Col md lg={6}>
        <div style={style}>
            <h2>{Translations['createNewPoll']}</h2>
        </div>
        
      <div className="form-group" style={style}>
        <label htmlFor="name">{Translations['pollName']}</label>
        <input style={textStyle} type="text" id="example1" className="form-control form-control-lg" />
      </div>

      <div className="form-group" style={style}>
        <label htmlFor="description">{Translations['pollDescription']}</label>
        <textarea style={textStyle} rows={6} type="text" id="example1" className="form-control form-control-lg" />
      </div>
      </Col>
      <Col md lg={4}>
        <div className='preview-panel' id='preview-image'>
            <div className='load-input'>
                <input id='image-loader' type='file' value={Translations['loadImg']} onchange={loadImage(this)}></input>
            </div>
            <img id='preview' alt='poll image' className='pre-image' ></img>
        </div>
      </Col>
      </Row>
        </Container>
    )
}

export default CreatePoll
